#include <iostream>

void swap(int& a, int& b) {
    int temp = a;
    a = b;
    b = temp;
}

void reverseArray(int arr[], int n) {
    int i, j;
    for (i = 0; i < n / 2; i++) {
        j = n - i - 1;
        swap(arr[i], arr[j]);
    }
}

